<?phprequire_once 'controller_helper.php';class Login extends controller_helper {    function __construct() {        parent::__construct();    }    function index() {        $this->alreadyLoggedIn();        $this->addViewData('info', array('Please login to continue'));        if ($this->input->server('REQUEST_METHOD') === 'POST') {            $this->form_validation->set_rules('username', 'Username', 'required');            $this->form_validation->set_rules('password', 'Password', 'required');            if ($this->form_validation->run() == FALSE) {                $this->addViewData('error', $this->getErrors(validation_errors()));            } else if ($this->persistence->user_has_identity() == FALSE) {                $this->addViewData('error', array('Incorrect username and password combination.'));            } else {                redirect('comision', 'refresh');                exit();            }        }        $this->loadview('login');    }    function logout() {        $this->session->sess_destroy();        redirect('login', 'refresh');    }}?>